#View to prepare data for calculation of correlations between books.
DROP view IF EXISTS V_corelation ;
CREATE VIEW V_corelation
AS 
SELECT
  a.isbn ,
  b.isbn AS isbn_2 ,
  COUNT(*) AS N ,
  SUM(a.review_int) AS Sum_rating ,
  SUM(b.review_int) AS Sum_2_rating ,
  SUM(a.review_int * b.review_int) AS Sum_dotProduct ,
  SUM(a.review_int * a.review_int) AS square_sum_rating ,
  SUM(b.review_int * b.review_int) AS square_sum_2_rating  
FROM
  review a
  JOIN review b ON a.user_id = b.user_id
WHERE
  a.isbn < b.isbn      	#To obtain every combinantion for calculating Pearson Correlation.
GROUP BY 
  1, 2 
  HAVING
  N >= 2;				#having atleast 2 occurence.

    
