#To calculate Co-Relations between books using pearson correlation coefficient
DROP TABLE IF EXISTS suggesstion_books ;
CREATE TABLE suggesstion_books AS
	SELECT
	  isbn ,
	  isbn_2 ,
      #formula given in the report.
	  (N * Sum_dotProduct - Sum_rating * Sum_2_rating) /
      (SQRT(N * square_sum_rating - Sum_rating * Sum_rating) *
      SQRT(N * square_sum_2_rating - Sum_2_rating * Sum_2_rating)) AS correlation
	FROM
	  V_corelation
	GROUP BY 
	  1, 2 
	ORDER BY
	  1, 3 DESC ;
	  
      
