#Trustworthiness of the User
SELECT users.user_id, users.first_name, users.last_name,
AVG(review_rating.review_int) as trustworthiness
FROM users, review_rating, review,review_gets_rated
WHERE users.user_id = review.user_id
AND review.review_id = review_gets_rated.review_id
AND review_gets_rated.review_rating_id = review_rating.review_rating_id
GROUP BY user_id;



#Likings of the user.
SELECT users.first_name, users.last_name,
titles.title FROM users, likes, titles
WHERE users.user_id = likes.user_id
AND titles.isbn = likes.isbn
AND users.first_name = "USER1";

#Location of a user.
SELECT users.first_name, users.age, users.gender,
region.region_name,country.country_name,city.city_name,location.full_address
FROM users,accesses_from_location, region,country,city,location
WHERE users.user_id = accesses_from_location.user_id
AND accesses_from_location.location_id = location.location_id
AND location.city_id = city.city_id
AND city.country_id=country.country_id
And country.region_id=region.region_id
AND users.first_name = "USER1";
	

#Close Proximity users
SELECT users.user_id,users.first_name
FROM users,accesses_from_location,city,location
WHERE users.user_id = accesses_from_location.user_id
AND accesses_from_location.location_id = location.location_id
AND location.city_id = city.city_id
AND city.city_name in(
SELECT city.city_name
FROM users,accesses_from_location,city,location
WHERE users.user_id = accesses_from_location.user_id
AND accesses_from_location.location_id = location.location_id
AND location.city_id = city.city_id
AND users.first_name = "USER1");


#Access time of the user. 
SELECT users.first_name, users.last_name,
AVG(TIME_TO_SEC(access_time.access_duration))/60/60 as Average_Access_in_Hours
FROM users, has_account_access_time, access_time
WHERE users.user_id = has_account_access_time.user_id
AND access_time.access_time_id = has_account_access_time.access_time_id
GROUP BY users.user_id;


